example.code:

var settings =
    new KarmaStartSettings
    {
       ConfigFile = "karma.conf.js"
    };
KarmaStart(settings);


var settings =
    new KarmaStartSettings
    {
        RunMode = KarmaRunMode.Local,
        ConfigFile = "karma.conf.js"
    };
KarmaStart(settings);


var settings =
    new KarmaRunSettings
    {
       ConfigFile = "karma.conf.js"
    };
KarmaRun(settings);


var settings =
    new KarmaRunSettings
    {
        RunMode = KarmaRunMode.Local,
        ConfigFile = "karma.conf.js"
    };
KarmaRun(settings);


var settings =
    new KarmaSettings
    {
       ConfigFile = "karma.conf.js"
    };
KarmaInit(settings);


var settings =
    new KarmaSettings
    {
        RunMode = KarmaRunMode.Local,
        ConfigFile = "karma.conf.js"
    };
KarmaInit(settings);
